import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "hardhat/console.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";

/// @title StakeXperiend
/// @author crisolita
/// @notice this contract allow create stake with fixed APR
contract StakeXperiend is
	Initializable,
	AccessControlUpgradeable,
	ReentrancyGuardUpgradeable
{
	using SafeMathUpgradeable for uint256;
	uint256 public currentIdStake;
	address public tokenAddress;
	struct StakeInfo {
		uint256 APR;
		uint256 maxCupo;
		uint256 endTime;
		uint256 timeLockRewards;
		uint256 timeLockExit;
		uint256 currentCupo;
		string data;
	}
	struct UserStakeInfo {
		uint256 stakeId;
		uint256 balance;
		uint256 rewardPerSecond;
		uint256 lastTimeClaimed;
		uint256 pendingReward;
		uint initTime;
	}
	mapping(uint256 => StakeInfo) public stakes;
	mapping(address => UserStakeInfo[]) internal userStakes;
	mapping(address => uint256) public earned;

	modifier onlyOwner() {
		require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
		_;
	}
	modifier isInStake(address _user, uint256 _stakeId) {
		require(
			isUserInStake(_user, _stakeId),
			"This user is not in this stake"
		);
		_;
	}

	//Events
	event StakeCreated(
		uint256 APR,
		uint256 maxCupo,
		uint256 endTime,
		uint256 timeLockRewards,
		uint256 timeLockExit,
		uint256 id,
		string data
	);
	event StakeUpdated(
		uint256 APR,
		uint256 maxCupo,
		uint256 endTime,
		uint256 timeLockRewards,
		uint256 timeLockExit,
		uint256 id,
		string data
	);
	event UserStaked(uint256 balance, uint256 stakeId, address user);
	event UserClaimedReward(
		address user,
		uint256 stakeId,
		uint256 balanceClaimed,
		uint256 lastTimeClaimed
	);
	event UserExit(address user, uint256 stakeId);

	function initialize(address _tokenAddress) public initializer {
		__AccessControl_init();
		_setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
		tokenAddress = _tokenAddress;
	}

	function createStake(
		uint256 _APR,
		uint256 _maxCupo,
		uint256 _endTime,
		uint256 _timeLockRewards,
		uint256 _timeLockExit,
		string memory _data
	) public onlyOwner {
		require(
			_endTime > block.timestamp,
			"The endTime should be greater than now"
		);
		require(_APR <= 100, "APR should be lower than 100");
		currentIdStake++;
		StakeInfo memory s = StakeInfo(
			_APR,
			_maxCupo,
			_endTime,
			_timeLockRewards,
			_timeLockExit,
			_maxCupo,
			_data
		);
		stakes[currentIdStake] = s;
		emit StakeCreated(
			_APR,
			_maxCupo,
			_endTime,
			_timeLockRewards,
			_timeLockExit,
			currentIdStake,
			_data
		);
	}

	function infoStakeByUser(
		uint256 _stakeId,
		address _user
	) public view returns (UserStakeInfo memory) {
		uint256 i = indexForUserInfoStakeById(_user, _stakeId);
		return userStakes[_user][i];
	}

	function updateStake(
		uint256 id,
		uint256 _APR,
		uint256 _maxCupo,
		uint256 _endTime,
		uint256 _timeLockRewards,
		uint256 _timeLockExit,
		string memory _data
	) public onlyOwner {
		require(id <= currentIdStake, "This id does not exist");
		require(
			_endTime > block.timestamp,
			"The endTime should be greater than now"
		);
		require(_APR <= 100, "APR should be lower than 100");
		StakeInfo memory s = StakeInfo(
			_APR,
			_maxCupo,
			_endTime,
			_timeLockRewards,
			_timeLockExit,
			stakes[id].currentCupo,
			_data
		);
		stakes[id] = s;
		emit StakeUpdated(
			_APR,
			_maxCupo,
			_endTime,
			_timeLockRewards,
			_timeLockExit,
			id,
			_data
		);
	}

	function getRewards(
		uint256 _stakeId
	) public isInStake(msg.sender, _stakeId) nonReentrant {
		require(
			block.timestamp > stakes[_stakeId].timeLockRewards,
			"No time to get rewards yet"
		);
		updatePendingReward(msg.sender, _stakeId);
		uint256 i = indexForUserInfoStakeById(msg.sender, _stakeId);
		require(
			userStakes[msg.sender][i].pendingReward > 0,
			"Do not have rewards"
		);
		require(
			ERC20(tokenAddress).transfer(
				msg.sender,
				userStakes[msg.sender][i].pendingReward
			),
			"The transfer failed"
		);
		earned[msg.sender] = earned[msg.sender].add(
			userStakes[msg.sender][i].pendingReward
		);
		userStakes[msg.sender][i].pendingReward = 0;
		userStakes[msg.sender][i].lastTimeClaimed = block.timestamp;

		emit UserClaimedReward(
			msg.sender,
			_stakeId,
			userStakes[msg.sender][i].pendingReward,
			userStakes[msg.sender][i].lastTimeClaimed
		);
	}

	function updatePendingReward(address _user, uint256 _stakeId) internal {
		if (isUserInStake(_user, _stakeId)) {
			uint256 i = indexForUserInfoStakeById(_user, _stakeId);
			if (stakes[_stakeId].endTime > block.timestamp) {
				userStakes[_user][i].pendingReward = (
					block.timestamp.sub(userStakes[_user][i].lastTimeClaimed)
				).mul(userStakes[_user][i].rewardPerSecond);
				console.log("Hola");
			} else {
				console.log("Chao");

				userStakes[_user][i].pendingReward = (
					stakes[_stakeId].endTime.sub(
						userStakes[_user][i].lastTimeClaimed
					)
				).mul(userStakes[_user][i].rewardPerSecond);
			}
		}
	}

	function exit(
		uint256 _stakeId
	) public isInStake(msg.sender, _stakeId) nonReentrant {
		require(
			block.timestamp > stakes[_stakeId].timeLockExit,
			"No time to get rewards yet"
		);
		updatePendingReward(msg.sender, _stakeId);
		uint256 i = indexForUserInfoStakeById(msg.sender, _stakeId);
		require(
			userStakes[msg.sender][i].balance > 0,
			"Do not have any balance"
		);
		require(
			ERC20(tokenAddress).transfer(
				msg.sender,
				userStakes[msg.sender][i].balance.add(
					userStakes[msg.sender][i].pendingReward
				)
			),
			"Transfer failed"
		);
		earned[msg.sender] = earned[msg.sender].add(
			userStakes[msg.sender][i].pendingReward
		);
		UserStakeInfo memory usi = UserStakeInfo(0, 0, 0, 0, 0, 0);
		userStakes[msg.sender][i] = usi;

		emit UserExit(msg.sender, _stakeId);
	}

	function fundThis(uint256 _balance) public {
		ERC20(tokenAddress).transferFrom(msg.sender, address(this), _balance);
	}

	function withdrawTokens(
		uint256 _amount,
		address _recipient
	) public onlyOwner {
		ERC20(tokenAddress).transfer(_recipient, _amount);
	}

	function indexForUserInfoStakeById(
		address _user,
		uint256 _stakeId
	) internal view returns (uint256 index) {
		if (userStakes[_user].length > 0) {
			for (uint256 i = 0; i < userStakes[_user].length; i++) {
				if (userStakes[_user][i].stakeId == _stakeId) {
					return i;
				}
			}
		}
	}

	function isUserInStake(
		address _user,
		uint256 _stakeId
	) public view returns (bool itIs) {
		if (userStakes[_user].length > 0) {
			for (uint256 i = 0; i < userStakes[_user].length; i++) {
				if (userStakes[_user][i].stakeId == _stakeId) {
					return true;
				}
			}
		} else {
			return false;
		}
	}

	function userStake(uint256 _balance, uint256 _stakeId) public {
		require(
			block.timestamp < stakes[_stakeId].endTime,
			"This stake is over"
		);
		require(
			stakes[_stakeId].currentCupo >= _balance,
			"This stake is complete"
		);
		require(
			ERC20(tokenAddress).transferFrom(
				msg.sender,
				address(this),
				_balance
			),
			"The token transfer failed"
		);
		updatePendingReward(msg.sender, _stakeId);

		uint256 i = indexForUserInfoStakeById(msg.sender, _stakeId);
		if (
			userStakes[msg.sender].length > 0 &&
			isUserInStake(msg.sender, _stakeId)
		) {
			userStakes[msg.sender][i].balance = userStakes[msg.sender][i]
				.balance
				.add(_balance)
				.add(userStakes[msg.sender][i].pendingReward);
			earned[msg.sender] = earned[msg.sender].add(
				userStakes[msg.sender][i].pendingReward
			);
			userStakes[msg.sender][i].pendingReward = 0;
			userStakes[msg.sender][i].initTime = block.timestamp;
			userStakes[msg.sender][i].lastTimeClaimed = block.timestamp;
			userStakes[msg.sender][i].rewardPerSecond = (
				(stakes[_stakeId].APR).mul(userStakes[msg.sender][i].balance)
			).div(31536000).div(100);
		} else {
			UserStakeInfo memory usi = UserStakeInfo(
				_stakeId,
				_balance,
				(stakes[_stakeId].APR.mul(_balance)).div(31536000).div(100),
				block.timestamp,
				0,
				block.timestamp
			);
			userStakes[msg.sender].push(usi);
			stakes[_stakeId].currentCupo -= _balance;
		}
		emit UserStaked(_balance, _stakeId, msg.sender);
	}
}
